import{_ as e,o as t,c as a,Q as o}from"./chunks/framework.32dba02b.js";const m=JSON.parse('{"title":"HTML（超文本标记语言）","description":"","frontmatter":{},"headers":[],"relativePath":"FE_Point/HTML/index.md","filePath":"FE_Point/HTML/index.md","lastUpdated":1708148918000}'),r={name:"FE_Point/HTML/index.md"},l=o('<h1 id="html-超文本标记语言" tabindex="-1">HTML（超文本标记语言） <a class="header-anchor" href="#html-超文本标记语言" aria-label="Permalink to &quot;HTML（超文本标记语言）&quot;">​</a></h1><blockquote><p><strong>HTML</strong>（超文本标记语言——HyperText Markup Language）是构成 Web 世界的一砖一瓦。它定义了网页内容的含义和结构。除 HTML 以外的其它技术则通常用来描述一个网页的表现与展示效果（如 <a href="https://developer.mozilla.org/zh-CN/docs/Web/CSS" target="_blank" rel="noreferrer">CSS</a>），或功能与行为（如 <a href="https://developer.mozilla.org/zh-CN/docs/Web/JavaScript" target="_blank" rel="noreferrer">JavaScript</a>）。</p><p>“超文本”（hypertext）是指连接单个网站内或多个网站间的网页的链接。链接是网络的一个基本方面。只要将内容上传到互联网，并将其与他人创建的页面相链接，你就成为了万维网的积极参与者。</p><p>HTML 使用“标记”（markup）来注明文本、图片和其他内容，以便于在 Web 浏览器中显示。HTML 标记包含一些特殊“元素”。</p><p>HTML 元素通过“标签”（tag）将文本从文档中引出，标签由在“<code>&lt;</code>”和“<code>&gt;</code>”中包裹的元素名组成，HTML 标签里的元素名不区分大小写。也就是说，它们可以用大写，小写或混合形式书写。例如，<code>&lt;title&gt;</code> 标签可以写成 <code>&lt;Title&gt;</code>，<code>&lt;TITLE&gt;</code> 或以任何其他方式。然而，习惯上与实践上都推荐将标签名全部小写。</p></blockquote><h2 id="参考" tabindex="-1">参考 <a class="header-anchor" href="#参考" aria-label="Permalink to &quot;参考&quot;">​</a></h2><ul><li><a href="https://developer.mozilla.org/zh-CN/docs/Web/HTML" target="_blank" rel="noreferrer">MDN HTML</a></li></ul>',4),d=[l];function c(n,i,s,_,p,h){return t(),a("div",null,d)}const f=e(r,[["render",c]]);export{m as __pageData,f as default};
